import { Button,  FormLabel , Typography,TextField,Box, Checkbox} from '@mui/material';

import React from 'react';
import { useState } from 'react';


const labelProps  ={
    mt:1,
    mb:1,
}

const AddMovie = () => {

    const [inputs, setInputs] = useState({
        title:"",
        description:"",
        posterUrl :"",
        releaseDate :"",
        featured:false,
    });
    const[actors, setActors] = useState([""]);
    const[actor, setActor] = useState("");




    const handleChange = (e) =>{
        setInputs((prevState) => ({...prevState,[e.target.name] :e.target.value}))
    }

    const handleSubmit =(e) =>{
        e.preventDefault();
        console.log(inputs);
    }

  return (
    <div > 
      <form height={"100vh"} onSubmit={handleSubmit}>
        <Box width={"50vw"} height={"50%"} padding={4} margin="auto" display={"flex"} flexDirection= "column" boxShadow ={"10px 10px 20px  #ccc"}>
        <Typography textAlign={"center"} variant='h5' fontFamily={"verdana"}>
        Add New Movie
        </Typography>
        < FormLabel sx={labelProps}>Title</ FormLabel>
        <TextField  value={inputs.title} onChange={handleChange} name="title"  variant="standard" margin="normal"/>
        < FormLabel sx={labelProps}>Description</ FormLabel>
        <TextField value={inputs.description} onChange={handleChange} name="description"  variant="standard" margin="normal"/>
        < FormLabel sx={labelProps}>Poster URL</ FormLabel>
        <TextField value={inputs.posterUrl} onChange={handleChange} name="posterUrl"  variant="standard" margin="normal"/>
        < FormLabel sx={labelProps}>Release Date</ FormLabel>
        <TextField value={inputs.releaseDate} onChange={(e) => e.target.value} name="releaseDate"  variant="standard" margin="normal"/>
        < FormLabel sx={labelProps}>Actor</ FormLabel>
        <Box display={"flex"} >
        <TextField name="actor" 
         variant="standard" margin="normal" />
        <Button onClick={() =>setActor([])}>Add</Button>
        </Box>
        < FormLabel sx={labelProps}>Featured</ FormLabel>
        <Checkbox name="featured" checked={inputs.featured} onClick ={(e) => setInputs((prevState) => ({
            ...prevState, featured : e.target.checked,
        }))} sx={{mr:"auto"}}/>
        <Button type="submit" variant="contained" sx={{width :"30%" ,margin:"auto" ,bgcolor:"#2b2d42" ,":hover" : {bgcolor:"#121217"}}}>
        Add New Movie
        </Button>
        </Box>
      </form>
    </div>
  )
}

export default AddMovie
