import React, { Fragment } from 'react'
import { useEffect,useState } from 'react';
import { useParams } from 'react-router-dom'
import { getMovieDetails } from '../../api-helpers/api-helpers';
import { newBooking } from '../../api-helpers/api-helpers';
import { FormLabel, TextField, Typography } from '@mui/material';
import {Box, Button} from '@mui/material';

const Booking = () => {

    const[movie, setMovie] = useState();
    const[inputs,setInputs] = useState({
      seatNumber :"" ,
      date: "",
    });

    const id = useParams().id;

    //console.log(id);
    useEffect(() =>{
      getMovieDetails(id).then((res) => setMovie(res.movie)).catch((err) => console.log(err))
    },[id]);
    console.log(movie);


    const handleChange = (e) =>{
    setInputs((prevState) =>({...prevState, [e.target.name] :e.target.value,
    }))
    }

    const handleSubmit = (e) =>{
      e.preventDefault();
      console.log(inputs);
      newBooking({...inputs, movie : movie._id})
    }

  return (
    <div>
      {movie && <Fragment>
        <Typography padding={3} fontFamily= "fantasy" variant ='h4' text-align={"center"}>Book Tickets of Movie : {movie.title}</Typography>
        <Box display={"flex"} justifyContent={"center"}>
        <Box display={"flex"} justifyContent={"column"} flexDirection ="column" padding={3} width= "50%" marginRight={'auto'}>
          <img width="80%" height={"300px"}  src={movie.posterUrl} alt={movie.title}/>
          <Box width='80%' marginTop={3} padding={2}>
            <Typography paddingTop={2}> {movie.description}</Typography>
            
            <Typography fontWeight={"bold"} marginTop={1}>
             Starrer : {movie.actors.map((actor) => actor + " " + " ")}
            </Typography>
            <Typography fontWeight={"bold"} marginTop={1}>
              Release Date : {new Date (movie.releaseDate).toDateString()}
            </Typography>
          </Box>
        </Box>
        <Box width={"50%"} paddingTop={3}>
          <form onSubmit={handleSubmit}>
            <Box padding={5} margin={"auto"} display="flex" flexDirection={"column"}>
            <FormLabel>Seat Number</FormLabel>
            <TextField value={inputs.seatNumber}  onChange={handleChange} name='seatNumber' type={'number'} margin="normal" variant="standard">seat Number</TextField>
            <TextField value={inputs.date}  onChange={handleChange} name='date' type={'date'} margin="normal" variant="standard">Booking Date</TextField>
            <Button type="submit" sx={{mt:3}}>BOOK NOW</Button>
            </Box>
          </form>
        </Box>
        </Box>
        </Fragment>}
    </div>
  )
}

export default Booking
