import React, { Fragment } from 'react'
import { useEffect,useState } from 'react';
import { useParams } from 'react-router-dom'
import { getMovieDetails } from '../../api-helpers/api-helpers';
import { Typography } from '@mui/material';
import Box from '@mui/material';

const Booking = () => {

    const[movie, setMovie] = useState();
    const id = useParams().id;

    //console.log(id);
    useEffect(() =>{
      getMovieDetails(id).then((res) => setMovie(res.movie)).catch((err) => console.log(err))
    },[id]);
    console.log(movie);
  return (
    <div>
      {movie && <Fragment>
        <Typography padding={3} fontFamily= "fantasy" variant ='h4' text-align={"center"}>Book Tickets of Movie : {movie.title}</Typography>
        <Box display={"flex"} justifyContent={"center"}>
        <Box display={"flex"} justifyContent={"column"} flexDirection ="column" padding={3} width= "50%">
          <img src={movie.posterUrl} alt={movie.title}/>
        </Box>
        </Box>
        </Fragment>}
    </div>
  )
}

export default Booking
